from colorama import Fore
import os
import time
import requests

def menu():
    os.system("cls")

    print(Fore.RED + "Made by anyx229")
    print()
    
    print(Fore.WHITE + "{1} Webhook Spammer")
    print(Fore.WHITE + "{2} Webhook Deleter")
    print(Fore.WHITE + "{3} Exit")

    print()
    option = input("Enter your option: ")

    if option == '1':
        WSpammer()
    elif option == '2':
        WDeleter()
    elif option == '3':
        os._exit(0)
    else:
        print(Fore.YELLOW + "Invalid option...")
        time.sleep(2)
        menu()

def WSpammer():
    os.system("cls")
    webhook = input("Enter the webhook URL: ")
    message = input("Enter the message to send: ")
    
    data = {"content": message}
    response = requests.post(webhook, json=data)

    while True:
        if response.status_code == 204:
            print(Fore.GREEN + f"{message} sent.")
        else:
            print(Fore.RED + f"Failed to send {message} (status {response.status_code})")
        if response.status_code == 429:
            print(Fore.RED + "Youre being rate limited.")

    input(Fore.CYAN + "Press Enter to return to menu...")
    menu()

def WDeleter():
    os.system("cls")
    webhook = input("Enter the webhook URL to delete: ")
    response = requests.delete(webhook)
    if response.status_code == 204:                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
        print(Fore.GREEN + "Webhook deleted successfully.")
    else:
         print(Fore.RED + f"Failed to delete webhook (status {response.status_code})")

    input(Fore.CYAN + "Press Enter to return to menu...")
    menu()

menu()
